import React from 'react';
import './HeroSection.css';
import { randomBinomial } from 'd3';
import IconMap from './map_image.png'

function HeroSection() {

    return (
        <div className="hero-container">
          <div className="hero-content">
            <h1 className='hero-content1'>Sustainability Project Management For Maximizing S - ROI</h1>
            <div className='here-content2'>
              <p>
              Welcome to our platform, where we provide a cutting-edge, 
              data-driven project management platform that integrates 
              Copernicus satellite data with advanced Sustainability 
              Return on Investment (S-ROI) calculations. 
              This approach enables organizations, governments, 
              and stakeholders to not only track their sustainability 
              efforts in real-time but also prioritize the highest-impact actions, 
              ensuring that taxpayers' money invested leads to measurable progress toward 
              global goals.
             </p>
          </div>
          <button className="learn-more-btn">Learn More</button>
          </div>
          <button className="start-measuring-btn">Start Measuring</button>

          {/* <img src={IconMap} alt="iconmap" className='hero-content1'/> */}
        </div>
      );
};

export default HeroSection
